import React, { useEffect } from "react";
import {BrowserRouter} from "react-router-dom";
import { Route } from 'react-router';

import {Grid} from "./Styles";
import Main from "./Main";
import Write from "./Write";
import {firestore} from "./firebase";




function App() {
  //함수형 컴포넌트에서는 componentDidMount대신 useEffect사용할 수 있음
  useEffect(() => {
    const Todo = firestore.collection("Todo");

    Todo.doc("A6vPJU6Ff5T615RtFawH").get().then((doc) => {
      if(doc.exists){
        console.log(doc.data());
      }
    });
    Todo
    .get()
    .then((docs) => {
      let Todo_data =[];
      docs.forEach((doc) => {
        console.log(doc);
        console.log(doc.data());
        console.log(doc.id);
        if (doc.exists){
          Todo_data = [...Todo_data, {id: doc.id, ...doc.data() }];
        }
      });

      console.log(Todo_data)
    });

// 데이터 추가하기

    Todo.add({
      "2021-03-01": [
        {
          todo_id: 11,
          datetime: "2021-03-01 10:10:00",
          contents: "산책가기1",
          completed: false,
        },
        {
          todo_id: 155555,
          datetime: "2021-03-01 10:15:00",
          contents: "산책가기2",
          completed: true,
        },
      ]}).then((docRef)=> {
      console.log(docRef);
      console.log(docRef.id)
    });

  });


  return (
    <Grid is_root bg="#ddd">
      <BrowserRouter>
        <Route path="/" exact component={Main} />
        <Route path="/write" exact component={Write} />
      </BrowserRouter>
    </Grid>
  );
}

export default App;
